    // NOTE:
    // - 1.05 is good for MagicMouse
    // - 1.25 is good for other mouse and faster
		d.commandHandler.zoomFactor = 1.25; //1.05; // 1.2;
		// Allow infinite canvas
		d.scrollMode = go.Diagram.InfiniteScroll;
  }
}
