//------------------------
// API Functions
//------------------------
function Diagram_getDiagramImage() {
	let result = null;
	const d = State_get( 'diagram' );
  if( d ) {
    //
    // NOTE: this is asynchronous
    //
		// example how to create a PNG image and set an
    // image src attribute.
    const writer = new draw2d.io.png.Writer();
    writer.marshal(d, function(png) {
      // -> this is an example: 
      //1$("#preview").attr("src",png);
    });
	}
	return( result );
}
function Diagram_getDataInfo( data, isShort ) {
  let result = '';
  if( data ) {
    let nodeType = 'NoType';
    if( data.from && data.to ) {
      nodeType = 'Link';
    } else {
      nodeType = ( data.linkToKey? 'LinkNode': 'Node' );
    }

    const label = data.text || data.category;
    result = `${nodeType} [${data.key}]: ${data.text}`;
    if( !isShort ) {
      result = result+`\nCategory: ${data.category}`+
             ( data.group? `\nMember of group: [${data.group}]`: '' );
    }
  }
  return( result );
}
function Diagram_getCurrentView() {
  const d =Diagram_getMainDiagram();
  if( d ) {
  	// Get current position
  	// d.getScrollLeft(), getScrollTop()
  	const position = { x: 0, y: 0 } ;
  	// Get current zoom scale
  	const scale = d.getZoom();
  	// Get grid visibility
  	const isGridOn = true;
  	// Define view info
  	const viewInfo = {
  		position: {
  			x: position.x,
  			y: position.y,
  		},
  		scale,
  		isGridOn,
  	};
	  return( viewInfo );
  } else {
    return( {} );
  }
}
function Diagram_setCurrentView( viewInfo ) {
  const d = Diagram_getMainDiagram();
  if( d && viewInfo ) {
    if( viewInfo.scale ) {
    	// Restore first scale (must be first)
    	d.setZoom( viewInfo.scale );
    }
    if( viewInfo.position ) {
    	// Restore position
    	d.setScrollLeft( viewInfo.position.x );
    	d.setScrollTop( viewInfo.position.y ); 
    }
    if( typeof( viewInfo.isGridOn ) == 'boolean' ) {
    	// Restore grid
